// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;


contract thetaTrade {
    struct tradeContract {
        uint thetaAmount;
        uint tfuelAmount;
        address sender_addr;
    }

    mapping(uint => tradeContract) public onGoingTrades;
    uint tokenAddress = 123;
    uint index = 0;

    function createNewThetaTrade(uint tfuelAmount) payable external returns (uint){
        tradeContract memory new_trade = tradeContract({tfuelAmount : tfuelAmount, thetaAmount : msg.value, sender_addr : msg.sender});
        index += 1;
        onGoingTrades[index] = new_trade;
        return index;
    }

    function getTradeInfo(uint trade_id) public view returns (uint, uint, address){
        return (onGoingTrades[trade_id].thetaAmount, onGoingTrades[trade_id].tfuelAmount, onGoingTrades[trade_id].sender_addr);
    }

//    function resolveThetaTrade() payable external returns (bool) {
